import { DatePipe } from '@angular/common';
import { Component, Injector } from '@angular/core';
import { AbstractControl, FormControl, Validators } from '@angular/forms';
import { Observable, of, switchMap, timer } from 'rxjs';
import { FwError } from 'src/app/common/constants';
import { genderList } from 'src/app/common/utils';
import { RolesGroup, User } from 'src/app/model/model';
import { BaseComponent } from 'src/app/page/base/BaseComponent';
import { RolesGroupService } from 'src/app/services/roles-group/roles-group.service';
import { UserService } from 'src/app/services/user/user.service';

@Component({
  selector: 'app-chinh-sua-nguoi-dung',
  templateUrl: './chinh-sua-nguoi-dung.component.html',
  styleUrls: ['./chinh-sua-nguoi-dung.component.scss'],
})
export class ChinhSuaNguoiDungComponent extends BaseComponent<User> {
  userDetail: User = new User();
  genderList = genderList;
  constructor(
    private userService: UserService,
    public rolesGroupService: RolesGroupService,
    public datepipe: DatePipe,
    private injector: Injector
  ) {
    super(injector);
  }
  rolesGroupSelected?: any;
  userStatus: boolean = false;
  detailForm = this.formBuilder.group({
    id: new FormControl([Validators.required]),
    birthday: new FormControl(),
    gender: new FormControl(),
    phoneNumber: new FormControl(),
    email: new FormControl([Validators.required]),
    fullname: new FormControl([Validators.required]),
    username: new FormControl([Validators.required]),
    status: new FormControl('', [Validators.required]),
    rolesGroup: new FormControl('', [Validators.required]),
    avatar: ['', []],
  });
  image: any =
    'data:image/png;base64,';
  ngOnInit(): void {
    // this.userDetail.rolesGroup = new RolesGroup();
    this.onInit();
  }
  override getDetail(id: any) {
    this.userService.getDetail(id).subscribe((res) => {
      if (FwError.THANHCONG == res.errorCode) {
        if (res.data) {
          this.userDetail = res.data;
          if (this.userDetail.rolesGroup) {
            this.rolesGroupSelected = this.userDetail.rolesGroup;
            this.rolesGroupSelected.label =
              this.rolesGroupSelected.id +
              (this.rolesGroupSelected.name
                ? ' - ' + this.rolesGroupSelected.name
                : '');
            this.rolesGroupSelected.track = this.rolesGroupSelected.id;
          }
          this.userStatus = this.userDetail.status == '1';
          if (this.userDetail.avatar) {
            this.byteToImage(this.userDetail.avatar, 'image/png').then(
              (item) => {
                this.image = item;
                this.detailForm.controls['avatar'].updateValueAndValidity();
              }
            );
          }
        }
      } else {
        this.toastrs.error(res.errorMessage);
      }
    });
  }
  override update() {
    this.isSubmit = true;
    if (this.detailForm.status == 'INVALID') {
      this.focusInValidForm();
      return;
    } else if (this.detailForm.status == 'PENDING') {
      this.toastrs.warning('Hệ thống đang xử lý! Vui lòng đợi');
      return;
    } else {
      if (this.userStatus) {
        this.userDetail.status = '1';
      } else {
        this.userDetail.status = '0';
      }
      this.userDetail.rolesGroup = this.rolesGroupSelected;
      this.userService
        .update(this.userDetail, this.selectedFiles)
        .subscribe((res) => {
          if (FwError.THANHCONG == res.errorCode) {
            if (res.data) {
              this.userDetail = res.data;
            }
            this._location.back();
          } else {
            this.toastrs.error(res.errorMessage);
          }
        });
    }
  }
  override create() {
    this.isSubmit = true;
    if (this.detailForm.status == 'INVALID') {
      this.focusInValidForm();
      return;
    } else if (this.detailForm.status == 'PENDING') {
      this.toastrs.warning('Hệ thống đang xử lý! Vui lòng đợi');
      return;
    } else {
      if (this.userStatus) {
        this.userDetail.status = '1';
      } else {
        this.userDetail.status = '0';
      }
      this.userDetail.rolesGroup = this.rolesGroupSelected;
      this.userService
        .create(this.userDetail, this.selectedFiles)
        .subscribe((res) => {
          if (FwError.THANHCONG == res.errorCode) {
            if (res.data) {
              this.userDetail = res.data;
            }
            this._location.back();
          } else {
            this.toastrs.error(res.errorMessage);
          }
        });
    }
  }
  override onFileSelected(event: any) {
    this.selectedFiles = event.target.files;
    if (this.selectedFiles && this.selectedFiles.length > 0)
      this.fileToImage(this.selectedFiles[0]).then(
        (item) => (this.image = item)
      );
  }
}
